package com.zerf.demo.leetcode;

/**
 * 〈一句话功能简述〉<br>
 * 〈〉
 *
 * @author Administrator
 * @create 20/8/12/0012
 * @since 1.0.0
 */
public class No459 {
    public boolean repeatedSubstringPattern(String s) {
        if (s.length() == 1) {
            return false;
        }
        char[] arr = s.toCharArray();
        Character last = null;
        for (int i = 0; i < arr.length / 2 + 1; i++) {
            if (last == null) {
                last = arr[i];
            } else if (arr[i] == last) {
                if (arr.length % i == 0) {
                    String substring = s.substring(0, i);
                    for (int j = i; j < arr.length; j += i) {
                        if (s.indexOf(substring, j) != j) {
                            break;
                        }
                        if (j + i == arr.length) {
                            return true;
                        }
                    }

                }
            }
        }
        return false;
    }

    public static void main(String[] args) {
        No459 no459 = new No459();
        System.out.println(no459.repeatedSubstringPattern("aabaab"));
        //System.out.println(no459.repeatedSubstringPattern(""));
        //String[] split = "abaababaab".split("abaab");
        //System.out.println(split.length);
    }
}